sysauto_script.script:
var rightNow = new GlideDateTime();
var due = new GlideTime();
due.setValue("24:00:00");
var dateTo = new GlideDateTime();
dateTo.add(due);
gs.info(dateTo);

var overdueNiTask = new GlideRecord("x_58872_needit_needit_task");
overdueNiTask.addActiveQuery();
overdueNiTask.addQuery("due_date", ">=", rightNow);
overdueNiTask.addQuery("due_date", "<", dateTo);
overdueNiTask.addQuery("state", "!=", "3");
overdueNiTask.query();
while (overdueNiTask.next()) {
	gs.info("Due soon NeedIt Task record: " + overdueNiTask.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTask', overdueNiTask, overdueNiTask.number, gs.getUserName());
}
